<script>
   function cek_pilihan_npsn() {
      var hasil = $('#pilihan_npsn').val();
      if (hasil == 1) {
         document.getElementById("field_temp_bantuan").style.display = "none";
         document.getElementById("form_upload_isian").style.display = "none";
         document.getElementById("form_npsn_isian").style.display = "block";
         document.getElementById("inputNPSN").attributes["required"] = "";
         document.getElementById("inputNPSN").disabled = false;
         document.getElementById("file_upload").required = false;
         document.getElementById("file_upload").value = "";
         document.getElementById("file_upload").disabled = true;
      }else if (hasil == 2) {
         document.getElementById("field_temp_bantuan").style.display = "block";
         document.getElementById("form_upload_isian").style.display = "block";
         document.getElementById("form_npsn_isian").style.display = "none";
         document.getElementById("inputNPSN").attributes["required"] = "none";
         document.getElementById("inputNPSN").value = "";
         document.getElementById("inputNPSN").disabled = true;
         document.getElementById("file_upload").required = true;
         document.getElementById("file_upload").disabled = false;
      }else{
         document.getElementById("field_temp_bantuan").style.display = "none";
         document.getElementById("form_upload_isian").style.display = "block";
         document.getElementById("form_npsn_isian").style.display = "none";
         document.getElementById("inputNPSN").attributes["required"] = "none";
         document.getElementById("inputNPSN").value = "";
         document.getElementById("inputNPSN").disabled = true;
         document.getElementById("file_upload").required = true;
         document.getElementById("file_upload").disabled = false;         
      }
   }
